function getQuote(){$.getJSON("https://random-quote-generator.herokuapp.com/api/quotes/random",function(t){parseQuote(t),updateTweet(t)})}function parseQuote(t){$("#quote").html("<div class='animated fadeIn'>"+(t.quote||"One thing you can't recycle is wasted time.")+"</div>"),$("#author").html("<div class='animated fadeIn'>- "+(t.author||"Anonymous")+"</div>")}function updateTweet(t){t.quote?$("#tweet").html("<a class='btn btn-default spacing' role='button' href='https://twitter.com/intent/tweet?text="+encodeURIComponent(t.quote).replace(/'/g,"%27")+" - "+encodeURIComponent(t.author).replace(/'/g,"%27")+"' target='_blank'><i class='fa fa-twitter navy'></i></a>"):$("#tweet").html("<a class='btn btn-default spacing' role='button' href='https://twitter.com/intent/tweet?text="+encodeURIComponent("One thing you can't recycle is wasted time.").replace(/'/g,"%27")+" - Anonymous' target='_blank'><i class='fa fa-twitter navy'></i></a>")}$(document).ready(function(){getQuote(),$("#refresh").on("click",function(){getQuote()}),$(".btn").mouseup(function(){$(this).blur()})});